import React from "react";
import clsx from "clsx";

const LunrAvatar = ({ emotion = "NEUTRAL", size = 10 }) => {
  const tailwindSize = `w-${size} h-${size}`;

  const emotionStyles = {
    NEUTRAL: "animate-floatSlow",
    HAPPY: "animate-floatSlow",
    FRIENDLY: "animate-floatSlow",
    EXCITED: "animate-floatSlow",
    SAD: "animate-sadFloat",
    MAD: "animate-none",
    LOVING: "animate-floatSlow twinkle",
  };

  const faceExpressions = {
    NEUTRAL: "M20,25 q10,-10 20,0",
    HAPPY: "M20,28 q10,10 20,0",
    SAD: "M20,35 q10,-10 20,0",
    MAD: "M20,28 q10,0 20,0",
    LOVING: "M20,26 q10,8 20,0",
    EXCITED: "M20,30 q10,10 20,0",
    FRIENDLY: "M20,27 q10,7 20,0",
  };

  return (
    <div
      className={clsx(
        "relative rounded-full bg-yellow-100 flex items-center justify-center shadow-lg transition-all duration-500",
        tailwindSize,
        emotionStyles[emotion]
      )}
    >
      <svg viewBox="0 0 60 60" className="w-4/5 h-4/5">
        {/* Head */}
        <circle cx="30" cy="30" r="28" fill="#fefce8" stroke="#ccc" strokeWidth="2" />

        {/* Eyes */}
        <circle cx="20" cy="22" r="3" fill="#333">
          <animate
            attributeName="r"
            values="3;3;3;1.5;3"
            dur="2.5s"
            repeatCount="indefinite"
            begin="0s"
          />
        </circle>
        <circle cx="40" cy="22" r="3" fill="#333">
          <animate
            attributeName="r"
            values="3;3;3;1.5;3"
            dur="2.5s"
            repeatCount="indefinite"
            begin="0.2s"
          />
        </circle>

        {/* Mouth */}
        <path
          d={faceExpressions[emotion]}
          stroke="#444"
          strokeWidth="2"
          fill="transparent"
        />
      </svg>
    </div>
  );
};

export default LunrAvatar;
