import React, { useState } from "react";

const MemoryForm = ({ onAddMemory }) => {
  const [image, setImage] = useState(null);
  const [journal, setJournal] = useState("");
  const [tags, setTags] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setImage(url);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!image || !journal) return;
    const memory = {
      image,
      journal,
      tags: tags.split(",").map(tag => tag.trim()),
      date: new Date().toLocaleDateString(),
    };
    onAddMemory(memory);
    setImage(null);
    setJournal("");
    setTags("");
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4">
      <label className="block text-sm font-medium text-gray-700">ðŸ“¸ Upload a photo</label>
      <input
        type="file"
        accept="image/*"
        onChange={handleImageChange}
        className="file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
      />

      {image && (
        <img src={image} alt="preview" className="rounded-lg h-48 object-cover border" />
      )}

      <textarea
        placeholder="Write your food story..."
        className="p-3 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        value={journal}
        onChange={(e) => setJournal(e.target.value)}
      />

      <input
        type="text"
        placeholder="Tag friends (comma-separated)"
        className="p-3 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

<button
  type="submit"
  className="bg-gradient-to-r from-pink-400 to-yellow-400 hover:from-pink-500 hover:to-yellow-500 text-white font-semibold py-2 px-4 rounded-full shadow-md text-sm transition transform hover:scale-105 active:scale-95 w-fit mx-auto"
>
  Save Memory
</button>


    </form>
  );
};

export default MemoryForm;
