import React, { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import API from "../../utils/api";
import GlobalHeader from "../../components/GlobalHeader/GlobalHeader";
import MemoryForm from "../../components/MemoryForm/MemoryForm";
import PostSparkForm from "../../components/PostSparkForm/PostSparkForm";

const MemorySparks = () => {
  const currentUser = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();

  const [sparks, setSparks] = useState([]);
  const [replyMap, setReplyMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeReply, setActiveReply] = useState(null);
  const [replyingToSpark, setReplyingToSpark] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchSparks = async () => {
      try {
        const res = await API.get("/sparks");
        const fetched = Array.isArray(res.data) ? res.data : res.data.sparks || [];
        setSparks(fetched);

        const replyData = {};
        for (const spark of fetched) {
          const threadRes = await API.get(`/memories/replies/${spark.id}`);
          replyData[spark.id] = threadRes.data.replies || [];
        }
        setReplyMap(replyData);
      } catch (err) {
        console.error("‚ùå Failed to fetch sparks:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchSparks();
  }, []);

  const handlePostSpark = async (data) => {
    try {
      const res = await API.post("/sparks", data);
      setSparks((prev) => [res.data.spark, ...prev]);
    } catch (err) {
      console.error("‚ùå Failed to post spark:", err);
    }
  };

  const handleReplySubmit = (newReply, sparkId) => {
    setReplyMap((prev) => ({
      ...prev,
      [sparkId]: [newReply, ...(prev[sparkId] || [])],
    }));
    setActiveReply(null);
    setReplyingToSpark(null);
  };

  const handleLove = async (sparkId) => {
    try {
      const res = await API.post("/sparks/love", { sparkId });
      const { loved } = res.data;

      setSparks((prev) =>
        prev.map((s) => {
          if (s.id !== sparkId) return s;
          const alreadyLoved = s.lovedBy?.includes(currentUser.uid);
          let updatedLovedBy = [...(s.lovedBy || [])];
          if (loved && !alreadyLoved) updatedLovedBy.push(currentUser.uid);
          if (!loved && alreadyLoved) updatedLovedBy = updatedLovedBy.filter((uid) => uid !== currentUser.uid);

          return {
            ...s,
            lovedBy: updatedLovedBy,
            loveCount: loved ? (s.loveCount || 0) + 1 : (s.loveCount || 1) - 1,
          };
        })
      );
    } catch (err) {
      console.error("‚ùå Love error:", err);
    }
  };

  const handleShare = async (sparkId) => {
    try {
      await API.post("/sparks/share", { sparkId });
      setSparks((prev) =>
        prev.map((s) =>
          s.id === sparkId ? { ...s, shareCount: (s.shareCount || 0) + 1 } : s
        )
      );
    } catch (err) {
      console.error("‚ùå Share error:", err);
    }
  };

  const replyCounts = useMemo(() => {
    const map = {};
    for (const id in replyMap) {
      map[id] = replyMap[id].length;
    }
    return map;
  }, [replyMap]);

  return (
    <>
      <GlobalHeader />
      <div className="max-w-6xl mx-auto px-4 py-6">
        <div className="flex justify-end mb-4">
          <button
            onClick={() => setShowModal(true)}
            className="bg-blue-600 text-white px-5 py-2 rounded-full text-sm shadow hover:bg-blue-700 transition"
          >
            ‚ú® Leave a Spark
          </button>
        </div>

        {showModal && (
          <div className="fixed inset-0 z-50 bg-black/40 flex items-center justify-center p-4">
            <div className="relative w-full max-w-md rounded-xl bg-white p-6">
              <button
                onClick={() => setShowModal(false)}
                className="absolute top-2 right-3 text-gray-400 hover:text-red-500 text-lg"
              >
                &times;
              </button>
              <PostSparkForm onSubmit={handlePostSpark} onClose={() => setShowModal(false)} />
            </div>
          </div>
        )}

        {loading ? (
          <p className="text-center text-gray-500">Loading sparks...</p>
        ) : sparks.length === 0 ? (
          <p className="text-center text-gray-500">No sparks yet</p>
        ) : (
          <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-4">
            {sparks.map((spark) => {
              const loved = spark.lovedBy?.includes(currentUser.uid);
              return (
                <div
                  key={spark.id}
                  className="bg-white/90 border border-blue-100 rounded-xl p-4 shadow flex flex-col"
                >
                  <p className="text-sm text-gray-700 italic mb-2">
                    ‚Äú{spark.journal.slice(0, 120)}{spark.journal.length > 120 ? "..." : ""}‚Äù
                  </p>

                  <div className="flex flex-wrap gap-2 mb-2">
                    <span className="bg-yellow-100 text-yellow-800 px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide shadow-sm ring-1 ring-yellow-300">
                      üß† {spark.mood}
                    </span>
                    <span className="bg-pink-100 text-pink-800 px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide shadow-sm ring-1 ring-pink-300">
                      üé® {spark.vibe}
                    </span>
                  </div>

                  <p className="text-xs text-gray-500 mb-2">
                    {spark?.location?.description || "Unknown"} ‚Ä¢ {spark.time}
                  </p>

                  <div
                    className="flex items-center gap-2 mt-1 cursor-pointer"
                    onClick={() => navigate(`/profile/${spark.name}`)}
                  >
                    <img
                      src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${spark.name}`}
                      alt={spark.name}
                      className="w-6 h-6 rounded-full border"
                    />
                    <span className="text-xs text-gray-700 font-medium">{spark.name}</span>
                  </div>

                  {/* Buttons */}
                  <div className="flex justify-between mt-4 border-t border-gray-200 pt-2 text-xs font-medium">
                    <button
                      className={`flex-1 py-2 transition flex items-center justify-center gap-1 ${
                        loved ? "text-red-600 font-bold" : ""
                      }`}
                      onClick={() => handleLove(spark.id)}
                    >
                      ‚ù§Ô∏è Love {spark.loveCount > 0 && <span className="text-gray-500">({spark.loveCount})</span>}
                    </button>
                    <button
                      className="flex-1 py-2 hover:bg-gray-100 transition flex items-center justify-center gap-1"
                      onClick={() => handleShare(spark.id)}
                    >
                      üîÅ Share {spark.shareCount > 0 && <span className="text-gray-500">({spark.shareCount})</span>}
                    </button>
                    <button
                      className="flex-1 py-2 hover:bg-gray-100 transition font-semibold flex items-center justify-center gap-1"
                      onClick={() => {
                        setReplyingToSpark(spark);
                        setActiveReply(spark.id);
                      }}
                    >
                      ü™û Reflect {replyCounts[spark.id] > 0 && <span className="text-blue-500">({replyCounts[spark.id]})</span>}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Memory Form Modal */}
      {activeReply && replyingToSpark && (
        <div className="fixed inset-0 z-50 bg-black/30 backdrop-blur-sm flex items-center justify-center p-4">
            <div className="relative w-full max-w-3xl max-h-[90vh] overflow-y-auto rounded-xl bg-white p-6 shadow-xl">
                <button
                    onClick={() => {
                        setActiveReply(null);
                        setReplyingToSpark(null);
                    }}
                    className="absolute top-2 right-3 text-gray-400 hover:text-red-500 text-lg"
                >
                    &times;
                </button>
                <h2 className="text-lg font-semibold text-gray-800 text-center mb-3">
                    Reflect on this Spark ‚ú®
                </h2>
                <MemoryForm
                    onAddMemory={(mem) => handleReplySubmit(mem, replyingToSpark.id)}
                    replyingTo={replyingToSpark.id}
                    isInline
                />
            </div>
        </div>
    )}

    </>
  );
};

export default MemorySparks;
