import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => {
          const createdAt = new Date(m.createdAt._seconds * 1000);
          return {
            ...m,
            dateFormatted: createdAt.toLocaleDateString("en-US", {
              month: "short",
              day: "numeric",
              year: "numeric",
            }),
            timeFormatted: createdAt.toLocaleTimeString("en-US", {
              hour: "numeric",
              minute: "2-digit",
              hour12: true,
            }),
          };
        });
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  const groupedMemories = memories.reduce((acc, mem) => {
    if (!acc[mem.dateFormatted]) acc[mem.dateFormatted] = [];
    acc[mem.dateFormatted].push(mem);
    return acc;
  }, {});

  return (
    <div className="flex flex-col gap-24 max-w-6xl mx-auto px-4 pt-10 relative">
      {Object.entries(groupedMemories).map(([date, group], i) => (
        <div key={i} className="relative w-full">
          <div className="flex justify-center relative w-full">
            {/* Memory Capsules Row */}
            <div className="flex gap-24 items-start relative">
              {/* Thread + Date */}
              <div className="relative flex items-center">
                {/* Horizontal thread */}
                <div className="h-[2px] w-14 bg-blue-300 opacity-70 rounded-full z-0" />

                {/* Date Tag */}
                <div className="absolute left-[-5.5rem] top-1/2 transform -translate-y-1/2 z-10">
                  <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md inline-block">
                    {date}
                  </div>
                </div>
              </div>

              {/* Memory Capsules */}
              {group.map((memory, j) => (
                <div key={j} className="relative flex flex-col items-center gap-2">
                  {/* Time Label */}
                  <div className="text-[11px] text-gray-500 font-medium mb-1">
                    {memory.timeFormatted}
                  </div>

                  {/* Capsule */}
                  <MemoryCard memory={memory} />

                  {/* Thread Line to next capsule */}
                  {j !== group.length - 1 && (
                    <div className="absolute top-1/2 left-full ml-4 h-[2px] w-16 bg-blue-300 opacity-70 transform -translate-y-1/2 z-0 rounded-full" />
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
