import React, { useState } from "react";
import MemoryForm from "./components/MemoryForm/MemoryForm";
import MemoryCard from "./components/MemoryCard/MemoryCard";

function App() {
  const [memories, setMemories] = useState([]);

  const handleAddMemory = (memory) => {
    setMemories([memory, ...memories]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#f7f8fa] to-[#e0e7ff] flex justify-center px-4 py-8">
      <div className="w-full max-w-2xl space-y-6">
        <h1 className="text-3xl font-bold text-center text-gray-800">Food Memory</h1>
        <div className="bg-white shadow-lg rounded-xl p-6">
          <MemoryForm onAddMemory={handleAddMemory} />
        </div>
        {memories.map((memory, index) => (
          <MemoryCard key={index} memory={memory} />
        ))}
      </div>
    </div>
  );
}

export default App;
