import React, { useState } from "react";
import MemoryForm from "./components/MemoryForm/MemoryForm.js";
import MemoryCard from "./components/MemoryCard/MemoryCard.js";

function App() {
  const [memories, setMemories] = useState([]);

  const handleAddMemory = (memory) => {
    setMemories([memory, ...memories]);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <h1 className="text-center text-xl font-semibold py-4">üçú Food Memory Capsule</h1>
      <MemoryForm onAddMemory={handleAddMemory} />
      <div className="px-4 py-2">
        {memories.map((memory, index) => (
          <MemoryCard key={index} memory={memory} />
        ))}
      </div>
    </div>
  );
}

export default App;
