import React, { useState, useEffect } from "react";
import usePlacesAutocomplete, { getGeocode, getLatLng } from "use-places-autocomplete";
import API from "../../utils/api";

const MOOD_OPTIONS = ["open", "nostalgic", "excited", "cozy", "peaceful"];
const VIBE_OPTIONS = ["friendly", "spicy", "warm", "simple", "comforting"];
const OCCASION_OPTIONS = ["hangout", "date", "family dinner", "birthday", "exploration"];
const TIME_OPTIONS = ["morning", "lunch", "evening", "midnight"];

const PostSparkForm = ({ onSubmit, onClose }) => {
  const [form, setForm] = useState({
    journal: "",
    mood: "",
    vibe: "",
    occasion: "",
    time: "",
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const [deviceLocation, setDeviceLocation] = useState(null);
  const [lunrSuggestion, setLunrSuggestion] = useState("");

  const {
    ready,
    value,
    setValue,
    suggestions: { status, data },
    clearSuggestions,
  } = usePlacesAutocomplete({ debounce: 300 });

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          const { latitude, longitude } = pos.coords;
          console.log("ðŸ“ Got user location:", latitude, longitude);
          setSelectedPlace({
            description: "Current Location",
            lat: latitude,
            lng: longitude,
          });
        },
        (err) => {
          console.error("âŒ Geolocation Error:", err);
        },
        { enableHighAccuracy: true }
      );
    } else {
      console.warn("ðŸš« Geolocation not supported.");
    }
  }, []);



  // ðŸ’¡ Fetch Lunr Suggestion with fallback to device location
  useEffect(() => {
    const fetchSuggestion = async () => {
      if (form.journal.trim().length < 4) return setLunrSuggestion("");

      try {
        const token = localStorage.getItem("token");
        const res = await API.post(
          "/ai/spark-suggest",
          {
            text: form.journal,
            location: selectedPlace
              ? {
                  description: selectedPlace.description,
                  lat: selectedPlace.lat,
                  lng: selectedPlace.lng,
                }
              : deviceLocation
              ? {
                  description: "Current Location",
                  lat: deviceLocation.lat,
                  lng: deviceLocation.lng,
                }
              : {},
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setLunrSuggestion(res.data.suggestion);
      } catch (err) {
        console.error("âŒ Lunr AI suggestion error:", err);
      }
    };

    const debounce = setTimeout(fetchSuggestion, 600);
    return () => clearTimeout(debounce);
  }, [form.journal, selectedPlace, deviceLocation]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.journal || !form.mood || !form.vibe || !form.occasion || !form.time || !(selectedPlace || deviceLocation)) return;

    const locationData = selectedPlace || {
      description: "Current Location",
      lat: deviceLocation.lat,
      lng: deviceLocation.lng,
    };

    const spark = {
      ...form,
      location: locationData,
      image: "",
    };

    onSubmit(spark);
    setForm({ journal: "", mood: "", vibe: "", occasion: "", time: "" });
    setValue("");
    setSelectedPlace(null);
    setLunrSuggestion("");
    onClose();
  };

  return (
    <div className="bg-white w-full max-w-md mx-auto rounded-xl p-6 shadow-xl border border-blue-100 space-y-4">
      <h2 className="text-lg font-semibold text-gray-800 text-center">Leave a Spark âœ¨</h2>

      <textarea
        value={form.journal}
        onChange={(e) => setForm({ ...form, journal: e.target.value })}
        placeholder="Whatâ€™s on your mind?"
        rows={2}
        className="w-full border border-gray-300 rounded-md p-2 text-sm"
      />

      {lunrSuggestion && (
        <div className="text-sm text-blue-600 italic mt-1 px-2">
          ðŸ’¡{" "}
          <span
            className="cursor-pointer hover:underline"
            onClick={() => setForm({ ...form, journal: lunrSuggestion })}
          >
            {lunrSuggestion}
          </span>
        </div>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
        {[["mood", MOOD_OPTIONS], ["vibe", VIBE_OPTIONS], ["occasion", OCCASION_OPTIONS], ["time", TIME_OPTIONS]].map(
          ([key, options]) => (
            <select
              key={key}
              value={form[key]}
              onChange={(e) => setForm({ ...form, [key]: e.target.value })}
              className="border border-gray-300 p-2 text-sm rounded"
            >
              <option value="">{key.charAt(0).toUpperCase() + key.slice(1)}</option>
              {options.map((o) => (
                <option key={o}>{o}</option>
              ))}
            </select>
          )
        )}
      </div>

      {/* Google Places Autocomplete */}
      <div className="relative">
        <input
          type="text"
          placeholder="Search place"
          value={value}
          onChange={(e) => {
            setValue(e.target.value);
            setSelectedPlace(null);
          }}
          disabled={!ready}
          className="w-full p-2 border border-gray-300 rounded text-sm"
        />
        {status === "OK" && data.length > 0 && (
          <ul className="absolute z-10 bg-white border border-gray-200 rounded shadow max-h-40 overflow-y-auto mt-1 text-sm w-full">
            {data.map((suggestion) => (
              <li
                key={suggestion.place_id}
                onClick={async () => {
                  const description = suggestion.description;
                  setValue(description, false);
                  clearSuggestions();
                  try {
                    const results = await getGeocode({ address: description });
                    const { lat, lng } = await getLatLng(results[0]);
                    setSelectedPlace({ description, lat, lng });
                  } catch (err) {
                    console.error("âŒ Location select error:", err);
                  }
                }}
                className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {suggestion.description}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="flex justify-between items-center pt-4">
        <button type="button" onClick={onClose} className="text-gray-500 text-sm hover:underline">
          Cancel
        </button>
        <button
          onClick={handleSubmit}
          className="bg-indigo-600 text-white px-4 py-2 rounded-md text-sm hover:bg-indigo-700"
        >
          Post
        </button>
      </div>
    </div>
  );
};

export default PostSparkForm;
