import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  const groupedMemories = memories.reduce((acc, mem) => {
    if (!acc[mem.dateFormatted]) acc[mem.dateFormatted] = [];
    acc[mem.dateFormatted].push(mem);
    return acc;
  }, {});

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => ({
          ...m,
          dateFormatted: new Date(m.createdAt._seconds * 1000).toLocaleDateString("en-US", {
            month: "short",
            day: "numeric",
            year: "numeric",
          }),
        }));
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  return (
    <div className="flex flex-col gap-20 max-w-6xl mx-auto px-4 pt-10 relative">
      {Object.entries(groupedMemories).map(([date, group], i) => (
  <div key={i} className="flex items-start gap-4 w-full">
    {/* Date Column */}
    <div className="w-28 shrink-0 pt-3 text-right">
      <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md inline-block">
        {date}
      </div>
    </div>

    {/* Memory Capsules Row */}
    <div className="flex gap-6 flex-wrap">
      {group.map((memory, j) => (
        <div key={j} className="relative">
          <MemoryCard memory={memory} />

          {/* Thread Connector if not last in group */}
          {j !== group.length - 1 && (
            <div className="absolute top-1/2 left-full h-[2px] w-6 bg-gradient-to-r from-blue-200 to-transparent transform -translate-y-1/2 z-0 rounded-full pointer-events-none" />
          )}
        </div>
      ))}
    </div>
  </div>
))}

    </div>
  );
};

export default HomeFeed;
