import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import GlobalHeader from "../../components/GlobalHeader/GlobalHeader";

const MemorySparks = () => {
  const currentUser = JSON.parse(localStorage.getItem("user"));
  const [sparks, setSparks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeReply, setActiveReply] = useState(null); // spark.id
  const [replyText, setReplyText] = useState("");
  const [replyImage, setReplyImage] = useState(null);

  useEffect(() => {
    const fetchSparks = async () => {
      try {
        const res = await API.get("/memories/sparks");
        setSparks(res.data.sparks);
      } catch (err) {
        console.error("‚ùå Failed to fetch sparks:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchSparks();
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) setReplyImage(file);
  };

  const submitReply = async (spark) => {
    if (!replyText.trim()) return alert("Write something first.");

    const reader = new FileReader();
    reader.onloadend = async () => {
      try {
        await API.post("/memories", {
          image: reader.result,
          journal: replyText,
          mood: spark.mood,
          vibe: spark.vibe,
          location: spark.location,
          respondingTo: {
            id: spark.id,
            creatorId: spark.userId,
            creatorName: spark.creatorName,
          },
        });

        setReplyText("");
        setReplyImage(null);
        setActiveReply(null);
        alert("‚úÖ Memory posted!");
      } catch (err) {
        console.error("‚ùå Failed to submit reply:", err);
        alert("Something went wrong.");
      }
    };

    if (replyImage) {
      reader.readAsDataURL(replyImage);
    } else {
      // No image? Send empty image placeholder
      await API.post("/memories", {
        image: "",
        journal: replyText,
        mood: spark.mood,
        vibe: spark.vibe,
        location: spark.location,
        respondingTo: {
          id: spark.id,
          creatorId: spark.userId,
          creatorName: spark.creatorName,
        },
      });

      setReplyText("");
      setActiveReply(null);
      alert("‚úÖ Memory posted!");
    }
  };

  return (
    <>
      <GlobalHeader />
      <div className="max-w-5xl mx-auto py-10 px-4">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
          ‚ú® Memory Sparks
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Loading sparks...</p>
        ) : sparks.length === 0 ? (
          <p className="text-center text-gray-500">
            No sparks right now. Be the first to start one!
          </p>
        ) : (
          <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
            {sparks.map((spark) => (
              <div
                key={spark.id}
                className="bg-white/90 backdrop-blur-md border border-blue-100 rounded-2xl p-5 shadow-xl hover:shadow-2xl transition duration-300 flex flex-col justify-between"
              >
                <img
                  src={spark.image}
                  alt="Memory Preview"
                  className="w-full h-48 object-cover rounded-xl mb-3"
                />

                <p className="text-sm text-gray-600 italic mb-2">
                  ‚Äú{spark.journal.slice(0, 120)}
                  {spark.journal.length > 120 ? "..." : ""}‚Äù
                </p>

                <div className="text-xs text-blue-700 font-semibold mb-2">
                  Vibe: {spark.vibe} ‚Ä¢ Mood: {spark.mood}
                </div>
                <p className="text-xs text-gray-500 mb-2">
                  {spark.city}, {spark.country} ‚Ä¢ {spark.time}
                </p>

                {/* üë§ Creator Avatar + Name */}
                <div
                  className="flex items-center gap-2 mt-2 cursor-pointer"
                  onClick={() => {
                    window.location.href = `/profile/${spark.creatorName}`;
                  }}
                >
                  <img
                    src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${spark.creatorName}`}
                    alt={spark.creatorName}
                    className="w-6 h-6 rounded-full border shadow-sm"
                  />
                  <span className="text-xs text-gray-700 font-medium">
                    {spark.creatorName}
                  </span>
                </div>

                <button
                  className="mt-4 w-full bg-gradient-to-r from-green-500 to-blue-500 text-white py-2 rounded-full font-semibold hover:from-green-600 hover:to-blue-600 transition"
                  onClick={() => {
                    setActiveReply(activeReply === spark.id ? null : spark.id);
                  }}
                >
                  {activeReply === spark.id ? "Cancel" : "Respond"}
                </button>

                {activeReply === spark.id && (
                  <div className="mt-4 space-y-2">
                    <textarea
                      placeholder="Write your memory response..."
                      className="w-full p-2 border rounded-md text-sm"
                      rows={3}
                      value={replyText}
                      onChange={(e) => setReplyText(e.target.value)}
                    />
                    <input type="file" onChange={handleImageChange} />
                    <button
                      className="bg-blue-600 hover:bg-blue-700 text-white text-sm px-4 py-2 rounded-full w-full"
                      onClick={() => submitReply(spark)}
                    >
                      Post Memory
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default MemorySparks;
