import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import usePlacesAutocomplete, { getGeocode, getLatLng } from "use-places-autocomplete";
import API from "../../utils/api";
import GlobalHeader from "../../components/GlobalHeader/GlobalHeader";
import MemoryForm from "../../components/MemoryForm/MemoryForm";

const MOOD_OPTIONS = ["open", "nostalgic", "excited", "cozy", "peaceful"];
const VIBE_OPTIONS = ["friendly", "spicy", "warm", "simple", "comforting"];
const OCCASION_OPTIONS = ["hangout", "date", "family dinner", "birthday", "exploration"];

const MemorySparks = () => {
  const currentUser = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();

  const [sparks, setSparks] = useState([]);
  const [replyMap, setReplyMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [activeReply, setActiveReply] = useState(null);
  const [showOptions, setShowOptions] = useState(false);

  const [newSpark, setNewSpark] = useState({
    journal: "",
    mood: "",
    vibe: "",
    occasion: "",
  });

  const [selectedPlace, setSelectedPlace] = useState(null);
  const {
    ready,
    value,
    setValue,
    suggestions: { status, data },
    clearSuggestions,
  } = usePlacesAutocomplete({ debounce: 300 });

  useEffect(() => {
    const fetchSparks = async () => {
      try {
        const res = await API.get("/sparks");
        const fetched = Array.isArray(res.data) ? res.data : res.data.sparks || [];
        setSparks(fetched);

        const replyData = {};
        for (const spark of fetched) {
          const threadRes = await API.get(`/memories/replies/${spark.id}`);
          replyData[spark.id] = threadRes.data.replies || [];
        }
        setReplyMap(replyData);
      } catch (err) {
        console.error("‚ùå Failed to fetch sparks:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchSparks();
  }, []);

  const handlePostSpark = async () => {
    const { journal, mood, vibe, occasion } = newSpark;
    if (!journal || !mood || !vibe || !occasion || !selectedPlace) return;

    try {
      const res = await API.post("/sparks", {
        ...newSpark,
        location: {
          description: selectedPlace.description,
          lat: selectedPlace.lat,
          lng: selectedPlace.lng,
        },
        time: "anytime",
        image: "",
      });

      setSparks((prev) => [res.data.spark, ...prev]);
      setNewSpark({ journal: "", mood: "", vibe: "", occasion: "" });
      setValue("");
      setSelectedPlace(null);
      setShowOptions(false);
    } catch (err) {
      console.error("‚ùå Failed to post spark:", err);
    }
  };

  const handleReplySubmit = (newReply, sparkId) => {
    setReplyMap((prev) => ({
      ...prev,
      [sparkId]: [newReply, ...(prev[sparkId] || [])],
    }));
    setActiveReply(null);
  };

  return (
    <>
      <GlobalHeader />
      <div className="max-w-6xl mx-auto px-4 py-6">
        <h1 className="text-2xl font-bold text-gray-800 mb-4 text-center">‚ú® Memory Sparks</h1>

        {/* üî• Spark Composer */}
        <div className="bg-white/90 border border-blue-100 rounded-xl shadow p-4 mb-6">
          <textarea
            placeholder="‚ú® Leave a spark... (e.g. craving sushi with a cozy vibe)"
            value={newSpark.journal}
            onFocus={() => setShowOptions(true)}
            onChange={(e) => setNewSpark({ ...newSpark, journal: e.target.value })}
            rows={1}
            className="w-full border border-gray-300 rounded-md p-2 text-sm resize-none"
          />

          {showOptions && (
            <div className="mt-3 grid grid-cols-1 md:grid-cols-4 gap-2">
              <select
                value={newSpark.mood}
                onChange={(e) => setNewSpark({ ...newSpark, mood: e.target.value })}
                className="p-2 border border-gray-300 rounded text-sm"
              >
                <option value="">Mood</option>
                {MOOD_OPTIONS.map((m) => (
                  <option key={m} value={m}>{m}</option>
                ))}
              </select>

              <select
                value={newSpark.vibe}
                onChange={(e) => setNewSpark({ ...newSpark, vibe: e.target.value })}
                className="p-2 border border-gray-300 rounded text-sm"
              >
                <option value="">Vibe</option>
                {VIBE_OPTIONS.map((v) => (
                  <option key={v} value={v}>{v}</option>
                ))}
              </select>

              <select
                value={newSpark.occasion}
                onChange={(e) => setNewSpark({ ...newSpark, occasion: e.target.value })}
                className="p-2 border border-gray-300 rounded text-sm"
              >
                <option value="">Occasion</option>
                {OCCASION_OPTIONS.map((o) => (
                  <option key={o} value={o}>{o}</option>
                ))}
              </select>

              {/* Google Places Autocomplete */}
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search place"
                  value={value}
                  onChange={(e) => {
                    setValue(e.target.value);
                    setSelectedPlace(null);
                  }}
                  disabled={!ready}
                  className="p-2 border border-gray-300 rounded text-sm w-full"
                />
                {status === "OK" && data.length > 0 && (
                  <ul className="absolute z-10 bg-white border border-gray-200 rounded shadow max-h-40 overflow-y-auto mt-1 text-sm">
                    {data.map((suggestion) => (
                      <li
                        key={suggestion.place_id}
                        onClick={async () => {
                          const description = suggestion.description;
                          setValue(description, false);
                          clearSuggestions();
                          try {
                            const results = await getGeocode({ address: description });
                            const { lat, lng } = await getLatLng(results[0]);
                            setSelectedPlace({ description, lat, lng });
                          } catch (err) {
                            console.error("‚ùå Location select error:", err);
                          }
                        }}
                        className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                      >
                        {suggestion.description}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            </div>
          )}

          {/* Post Button */}
          <div className="mt-3 text-right">
            <button
              onClick={handlePostSpark}
              className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 text-sm"
            >
              üöÄ Post Spark
            </button>
          </div>
        </div>

        {/* ‚ú® Spark Feed */}
        {loading ? (
          <p className="text-center text-gray-500">Loading sparks...</p>
        ) : sparks.length === 0 ? (
          <p className="text-center text-gray-500">No sparks yet</p>
        ) : (
          <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-4">
            {sparks.map((spark) => (
              <div
                key={spark.id}
                className="bg-white/90 border border-blue-100 rounded-xl p-4 shadow flex flex-col"
              >
                <p className="text-sm text-gray-700 italic mb-2">
                  ‚Äú{spark.journal.slice(0, 120)}{spark.journal.length > 120 ? "..." : ""}‚Äù
                </p>

                <div className="text-xs text-blue-700 font-medium mb-1">
                  Mood: {spark.mood} ‚Ä¢ Vibe: {spark.vibe}
                </div>
                <p className="text-xs text-gray-500 mb-2">
                  {spark?.location?.description || "Unknown"} ‚Ä¢ {spark.time}
                </p>

                <div
                  className="flex items-center gap-2 mt-1 cursor-pointer"
                  onClick={() => navigate(`/profile/${spark.name}`)}
                >
                  <img
                    src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${spark.name}`}
                    alt={spark.name}
                    className="w-6 h-6 rounded-full border"
                  />
                  <span className="text-xs text-gray-700 font-medium">{spark.name}</span>
                </div>

                <button
                  className="mt-4 w-full bg-green-500 text-white py-2 rounded-full text-sm hover:bg-green-600"
                  onClick={() => setActiveReply(spark.id)}
                >
                  Respond
                </button>

                {activeReply === spark.id && (
                  <div className="mt-4">
                    <MemoryForm
                      onAddMemory={(mem) => handleReplySubmit(mem, spark.id)}
                      replyingTo={spark.id}
                      isInline
                    />
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default MemorySparks;
