import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => {
          const createdAt = new Date(m.createdAt._seconds * 1000);
          return {
            ...m,
            dateFormatted: createdAt.toLocaleDateString("en-US", {
              month: "short",
              day: "numeric",
              year: "numeric",
            }),
            timeFormatted: createdAt.toLocaleTimeString("en-US", {
              hour: "numeric",
              minute: "2-digit",
              hour12: true,
            }),
          };
        });
        setMemories(formatted.reverse()); // Latest first
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  const groupedMemories = memories.reduce((acc, mem) => {
    if (!acc[mem.dateFormatted]) acc[mem.dateFormatted] = [];
    acc[mem.dateFormatted].push(mem);
    return acc;
  }, {});

  return (
    <div className="flex flex-col gap-24 max-w-7xl mx-auto px-4 pt-10">
      {Object.entries(groupedMemories).map(([date, group], i) => (
        <div key={i} className="relative w-full">
          <div className="flex items-end relative">
            {/* DATE BUBBLE */}
            <div className="shrink-0 mr-6 relative">
              <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md">
                {date}
              </div>
              <div className="absolute top-1/2 left-full h-[2px] w-8 bg-blue-300 rounded-full opacity-70 -translate-y-1/2 z-0" />
            </div>

            {/* TIME + CARD sequence */}
            {group.map((memory, j) => (
              <React.Fragment key={j}>
                {/* TIME TAG ABOVE THREAD */}
                <div className="flex flex-col items-center mx-2 relative">
                  <div className="mb-2 text-[11px] text-gray-500 font-medium">
                    {memory.timeFormatted}
                  </div>
                  {/* Thread segment */}
                  <div className="absolute top-[50%] -left-8 w-8 h-[2px] bg-blue-300 opacity-70 z-0 rounded-full pointer-events-none" />
                </div>

                {/* CARD */}
                <div className="relative mr-6">
                  <MemoryCard memory={memory} />
                  {/* Thread to next item (unless last) */}
                  {j !== group.length - 1 && (
                    <div className="absolute top-1/2 left-full ml-2 h-[2px] w-8 bg-blue-300 opacity-70 transform -translate-y-1/2 z-0 rounded-full" />
                  )}
                </div>
              </React.Fragment>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
