import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => {
          const createdAt = new Date(m.createdAt._seconds * 1000);
          return {
            ...m,
            dateFormatted: createdAt.toLocaleDateString("en-US", {
              month: "short",
              day: "numeric",
              year: "numeric",
            }),
            timeFormatted: createdAt.toLocaleTimeString("en-US", {
              hour: "numeric",
              minute: "2-digit",
              hour12: true,
            }),
          };
        });
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  const groupedMemories = memories.reduce((acc, mem) => {
    if (!acc[mem.dateFormatted]) acc[mem.dateFormatted] = [];
    acc[mem.dateFormatted].push(mem);
    return acc;
  }, {});

  return (
    <div className="flex flex-col gap-24 max-w-6xl mx-auto px-4 pt-10">
      {Object.entries(groupedMemories).map(([date, group], i) => (
        <div key={i} className="relative w-full">
          {/* Thread Row */}
          <div className="flex flex-col items-start w-full">
            {/* Top Row: Time Labels */}
            <div className="flex items-center gap-16 pl-[9rem] mb-2">
              {/* Empty space where the date tag sits */}
              {group.map((memory, j) => (
                <div key={`time-${j}`} className="w-[10rem] text-center">
                  <span className="text-[11px] text-gray-500 font-medium">
                    {memory.timeFormatted}
                  </span>
                </div>
              ))}
            </div>

            {/* Middle Row: Thread + Cards */}
            <div className="flex items-center relative w-full">
              {/* Date + Thread */}
              <div className="relative shrink-0 w-[9rem]">
                {/* Thread to first capsule */}
                <div className="absolute top-1/2 left-full h-[2px] w-6 bg-blue-300 rounded-full opacity-70 -translate-y-1/2 z-0" />

                {/* Date tag */}
                <div className="absolute top-1/2 right-full transform -translate-y-1/2 translate-x-full">
                  <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md inline-block">
                    {date}
                  </div>
                </div>
              </div>

              {/* Capsules with thread between */}
              <div className="flex items-center gap-16 relative">
                {group.map((memory, j) => (
                  <div key={j} className="relative">
                    {/* Card */}
                    <MemoryCard memory={memory} />

                    {/* Thread to next card */}
                    {j !== group.length - 1 && (
                      <div className="absolute top-1/2 left-full ml-4 h-[2px] w-16 bg-blue-300 opacity-70 transform -translate-y-1/2 z-0 rounded-full" />
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
