import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => ({
          ...m,
          dateFormatted: new Date(m.createdAt._seconds * 1000).toLocaleDateString("en-US", {
            month: "short",
            day: "numeric",
            year: "numeric",
          }),
        }));
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  return (
    <div className="flex flex-col gap-16 max-w-5xl mx-auto px-4 pt-10">
      {memories.map((memory, i) => (
        <div key={i} className="flex gap-6 items-start">
          {/* Date Bubble */}
          <div className="w-32 text-right pt-4">
            <div className="bg-blue-100 text-blue-700 text-xs font-medium px-2 py-1 rounded-full shadow">
              {memory.dateFormatted}
            </div>
          </div>

          {/* Memory Capsule */}
          <div className="flex-1">
            <MemoryCard memory={memory} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
