import React from "react";

const getAuraClass = (mood, vibe) => {
  // Choose color themes based on mood + vibe
  if (mood === "nostalgic") return "bg-gradient-to-r from-[#cbd5e1] via-white to-[#e0e7ff]";
  if (mood === "excited") return "bg-gradient-to-r from-[#bfdbfe] via-white to-[#ffe4e6]";
  if (mood === "cozy") return "bg-gradient-to-r from-[#fef9c3] via-white to-[#fce7f3]";
  if (mood === "peaceful") return "bg-gradient-to-r from-[#ccfbf1] via-white to-[#e0f2fe]";

  // Fallback for vibe-only cases
  if (vibe === "spicy") return "bg-gradient-to-r from-[#fecaca] via-white to-[#fee2e2]";
  if (vibe === "warm") return "bg-gradient-to-r from-[#fde68a] via-white to-[#fcd34d]";
  if (vibe === "simple") return "bg-gradient-to-r from-[#f1f5f9] via-white to-[#e2e8f0]";
  if (vibe === "comforting") return "bg-gradient-to-r from-[#ddd6fe] via-white to-[#f3e8ff]";

  return "bg-gradient-to-b from-[#e0f7ff] via-white to-[#f0eaff]";
};

const MemoryCard = ({ memory }) => {
  const auraClass = getAuraClass(memory.mood, memory.vibe);

  return (
    <div
  className={`
    relative ${auraClass}
    p-4 rounded-[2rem] shadow-xl w-[220px] border border-blue-100 ring-1 ring-blue-200
    backdrop-blur-md overflow-hidden
    transition-transform duration-300 ease-in-out hover:-translate-y-1 hover:shadow-[0_8px_24px_rgba(147,197,253,0.3)]
    hover:ring-2 hover:ring-blue-300 hover:ring-offset-1
  `}
>
  {/* Glow */}
  <div className="absolute inset-0 blur-2xl bg-blue-200 opacity-10 pointer-events-none" />

  <div className="relative flex flex-col items-center space-y-3 z-10">
    <div className="overflow-hidden rounded-2xl h-40 w-full border-4 border-white shadow-sm">
      <img
        src={memory.image}
        alt="memory"
        className="w-full h-full object-cover"
      />
    </div>

    {(memory.mood || memory.vibe) && (
      <div className="text-[11px] font-medium text-gray-600 italic text-center px-3 leading-tight">
        {memory.mood && memory.vibe
          ? `Felt ${memory.mood}, with a ${memory.vibe} touch.`
          : memory.mood
          ? `Felt ${memory.mood}.`
          : `Something ${memory.vibe} in the air.`}
      </div>
    )}

    <div className="text-center space-y-1 px-1">
      <p className="text-sm text-gray-800 font-serif italic leading-snug">“{memory.journal}”</p>
      <p className="text-[10px] text-gray-500">
        {memory.date}
        {memory.city && memory.country && (
          <span className="text-blue-600 ml-1">• {memory.city}, {memory.country}</span>
        )}
      </p>
    </div>

    {memory.tags.length > 0 && (
      <div className="flex flex-wrap justify-center gap-1 text-[10px] text-blue-700 font-medium">
        {memory.tags.map((tag, i) => (
          <span key={i} className="bg-blue-100 px-2 py-0.5 rounded-full shadow">
            {tag}
          </span>
        ))}
      </div>
    )}
  </div>
</div>

  );
};

export default MemoryCard;
