import React, { useState, useEffect } from "react";
import usePlacesAutocomplete, { getGeocode, getLatLng } from "use-places-autocomplete";
import API from "../../utils/api";

const MOOD_OPTIONS = ["open", "nostalgic", "excited", "cozy", "peaceful"];
const VIBE_OPTIONS = ["friendly", "spicy", "warm", "simple", "comforting"];
const OCCASION_OPTIONS = ["hangout", "date", "family dinner", "birthday", "exploration"];
const TIME_OPTIONS = ["morning", "lunch", "evening", "midnight"];

const PostSparkForm = ({ onSubmit, onClose }) => {
  const [form, setForm] = useState({ journal: "", mood: "", vibe: "", occasion: "", time: "" });
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [lunrSuggestion, setLunrSuggestion] = useState("");
  const [locationGranted, setLocationGranted] = useState(false);
  const [locationDenied, setLocationDenied] = useState(false);
  const [locationErrorMessage, setLocationErrorMessage] = useState("");

  const {
    ready,
    value,
    setValue,
    suggestions: { status, data },
    clearSuggestions,
  } = usePlacesAutocomplete({ debounce: 300 });

  const handleUseCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setSelectedPlace({
            description: "Current Location",
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
          setLocationGranted(true);
          setLocationDenied(false);
          console.log("üìç Location granted:", position.coords);
        },
        (error) => {
          console.error("‚ùå Location error:", error);
          setLocationErrorMessage(
            error.code === 1
              ? "User denied Geolocation. Please allow it in your browser settings."
              : "Location access is currently unavailable. Try again."
          );
          setLocationGranted(false);
          setLocationDenied(true);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0,
        }
      );
    } else {
      console.error("üö´ Geolocation not supported by this browser.");
      setLocationErrorMessage("Geolocation is not supported by this browser.");
      setLocationDenied(true);
    }
  };


  useEffect(() => {
    const fetchSuggestion = async () => {
      if (form.journal.trim().length < 4 || !selectedPlace) return setLunrSuggestion("");
      try {
        const token = localStorage.getItem("token");
        const res = await API.post(
          "/ai/spark-suggest",
          { text: form.journal, location: selectedPlace },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setLunrSuggestion(res.data.suggestion);
      } catch (err) {
        console.error("‚ùå Lunr AI suggestion error:", err);
      }
    };

    const debounce = setTimeout(fetchSuggestion, 600);
    return () => clearTimeout(debounce);
  }, [form.journal, selectedPlace]);

  const splitSuggestion = (text) => {
    const cleaned = text.replace(/^["']|["']$/g, "").replace(/üí°/g, "").trim();

    // Example: "How about trying Strings Ramen Shop Madison at 311 N Frances St, Madison"
    const introRegex = /^(.*?)(?=\s(?:at|on|in|near|inside)\s)/i;
    const introMatch = cleaned.match(introRegex);

    const intro = introMatch ? introMatch[1].trim() : "Suggested Spot";
    const place = cleaned.replace(intro, "").replace(/^[\s:\-]+/, "").trim().replace(/\?$/, "");

    return { intro, place };
  };



  const handleAISuggestionClick = async () => {
    const { place } = splitSuggestion(lunrSuggestion);
    setValue(place, true);
    clearSuggestions();

    const waitForSuggestions = () => {
      return new Promise((resolve, reject) => {
        let attempts = 0;
        const interval = setInterval(() => {
          if (status === "OK" && data.length > 0) {
            const match = data.find((d) => d.description.toLowerCase().includes(place.toLowerCase()));
            if (match) {
              clearInterval(interval);
              resolve(match);
            }
          }
          if (++attempts >= 10) {
            clearInterval(interval);
            reject("‚ùå Could not find place in dropdown");
          }
        }, 400);
      });
    };

    try {
      const match = await waitForSuggestions();
      setValue(match.description, false);
      clearSuggestions();
      const geo = await getGeocode({ address: match.description });
      const { lat, lng } = await getLatLng(geo[0]);
      setSelectedPlace({ description: match.description, lat, lng });
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.journal || !form.mood || !form.vibe || !form.occasion || !form.time || !selectedPlace) return;
    const spark = { ...form, location: selectedPlace, image: "" };
    onSubmit(spark);
    setForm({ journal: "", mood: "", vibe: "", occasion: "", time: "" });
    setValue("");
    setSelectedPlace(null);
    setLunrSuggestion("");
    onClose();
  };

  return (
    <div className="bg-white w-full max-w-md mx-auto rounded-xl p-6 shadow-xl border border-blue-100 space-y-4">
      <h2 className="text-lg font-semibold text-gray-800 text-center">Leave a Spark ‚ú®</h2>

      {!locationGranted ? (
        <div className="text-center space-y-2">
          <p className="text-sm text-gray-600 mb-2">
            To help us suggest meaningful places around you, please enable location access.
          </p>
          <button onClick={handleUseCurrentLocation} className="text-sm text-blue-600 underline">
            üìç Enable My Location
          </button>
          {locationDenied && (
            <p className="text-xs text-red-500 mt-2">{locationErrorMessage}</p>
          )}
        </div>
      ) : (
        <>
          <textarea
            value={form.journal}
            onChange={(e) => setForm({ ...form, journal: e.target.value })}
            placeholder="What‚Äôs on your mind?"
            rows={2}
            className="w-full border border-gray-300 rounded-md p-2 text-sm"
          />

{lunrSuggestion && (() => {
  const { intro, place } = splitSuggestion(lunrSuggestion);
  return (
    <div className="flex flex-col items-center mt-2 space-y-2">
      <p className="text-sm font-medium text-indigo-600">{intro}</p>
      <div
        onClick={handleAISuggestionClick}
        className="cursor-pointer transition-transform duration-300 ease-out transform hover:scale-[1.02] bg-indigo-50 border border-indigo-200 rounded-xl shadow-md p-4 w-full max-w-sm"
      >
        <p className="text-base text-gray-800 font-semibold group-hover:text-indigo-900 transition-colors duration-200">
          {place}
        </p>
      </div>
    </div>
  );
})()}


          <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
            {[["mood", MOOD_OPTIONS], ["vibe", VIBE_OPTIONS], ["occasion", OCCASION_OPTIONS], ["time", TIME_OPTIONS]].map(
              ([key, options]) => (
                <select
                  key={key}
                  value={form[key]}
                  onChange={(e) => setForm({ ...form, [key]: e.target.value })}
                  className="border border-gray-300 p-2 text-sm rounded"
                >
                  <option value="">{key.charAt(0).toUpperCase() + key.slice(1)}</option>
                  {options.map((o) => (
                    <option key={o}>{o}</option>
                  ))}
                </select>
              )
            )}
          </div>

          <div className="relative">
            <input
              type="text"
              placeholder="Search place"
              value={value}
              onChange={(e) => {
                setValue(e.target.value);
                setSelectedPlace(null);
              }}
              disabled={!ready}
              className="w-full p-2 border border-gray-300 rounded text-sm"
            />
            {status === "OK" && data.length > 0 && (
              <ul className="absolute z-10 bg-white border border-gray-200 rounded shadow max-h-40 overflow-y-auto mt-1 text-sm w-full">
                {data.map((suggestion) => (
                  <li
                    key={suggestion.place_id}
                    onClick={async () => {
                      const description = suggestion.description;
                      setValue(description, false);
                      clearSuggestions();
                      try {
                        const results = await getGeocode({ address: description });
                        const { lat, lng } = await getLatLng(results[0]);
                        setSelectedPlace({ description, lat, lng });
                      } catch (err) {
                        console.error("‚ùå Location select error:", err);
                      }
                    }}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                  >
                    {suggestion.description}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <div className="flex justify-between items-center pt-4">
            <button onClick={onClose} className="text-gray-500 text-sm hover:underline">
              Cancel
            </button>
            <button
              onClick={handleSubmit}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md text-sm hover:bg-indigo-700"
            >
              Post
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default PostSparkForm;
