import React, { useState } from "react";

const SparkCard = ({
  spark,
  loved,
  reflects,
  userMap,
  navigate,
  onLove,
  onShare,
  onReply,
}) => {
  const [showReflects, setShowReflects] = useState(false);

  const handleToggleReflect = () => {
    console.log(`ü™û Toggled reflects for sparkId: ${spark.id}`);
    setShowReflects((prev) => !prev);
  };

  return (
    <div className="bg-white/90 border border-blue-100 rounded-xl p-4 shadow flex flex-col">
      <p className="text-sm text-gray-700 italic mb-2">
        ‚Äú{spark.journal.slice(0, 120)}{spark.journal.length > 120 ? "..." : ""}‚Äù
      </p>

      <div className="flex flex-wrap gap-2 mb-2">
        <span className="bg-yellow-100 text-yellow-800 px-3 py-1 rounded-full text-xs font-semibold">
          üß† {spark.mood}
        </span>
        <span className="bg-pink-100 text-pink-800 px-3 py-1 rounded-full text-xs font-semibold">
          üé® {spark.vibe}
        </span>
      </div>

      <p className="text-xs text-gray-500 mb-2">
        {spark?.location?.description || "Unknown"} ‚Ä¢ {spark.time}
      </p>

      <div
        className="flex items-center gap-2 mt-1 cursor-pointer"
        onClick={() => navigate(`/profile/${spark.name}`)}
      >
        <img
          src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${spark.name}`}
          alt={spark.name}
          className="w-6 h-6 rounded-full border"
        />
        <span className="text-xs text-gray-700 font-medium">{spark.name}</span>
      </div>

      <div className="flex justify-between mt-4 border-t border-gray-200 pt-2 text-xs font-medium">
        <button
          className={`flex-1 py-2 transition flex items-center justify-center gap-1 ${
            loved ? "text-red-600 font-bold" : ""
          }`}
          onClick={() => onLove(spark.id)}
        >
          ‚ù§Ô∏è Love {spark.loveCount > 0 && <span className="text-gray-500">({spark.loveCount})</span>}
        </button>
        <button
          className="flex-1 py-2 hover:bg-gray-100 transition flex items-center justify-center gap-1"
          onClick={() => onShare(spark.id)}
        >
          üîÅ Share {spark.shareCount > 0 && <span className="text-gray-500">({spark.shareCount})</span>}
        </button>
        <button
          className="flex-1 py-2 hover:bg-gray-100 transition font-semibold flex items-center justify-center gap-1"
          onClick={handleToggleReflect}
        >
          ü™û Reflect {reflects.length > 0 && <span className="text-blue-500">({reflects.length})</span>}
        </button>
      </div>

      {showReflects && (
        <div className="mt-4 space-y-3 border-t pt-3 border-dashed">
          <button
            onClick={() => onReply(spark)}
            className="text-[12px] text-indigo-600 hover:underline font-semibold mb-2"
          >
            ‚ûï Add Your Reflection
          </button>

          {reflects.map((mem, i) => (
            <div key={i} className="flex items-start gap-2">
              <img
                src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${mem.name}`}
                alt={mem.name}
                className="w-7 h-7 rounded-full border"
              />
              <div className="text-xs text-gray-700 flex-1">
                <div className="flex justify-between">
                  <p className="font-semibold">
                    {userMap[mem.userId] || mem.name || mem.email?.split("@")[0]}
                  </p>
                  {mem.meaning && (
                    <span className="bg-blue-100 text-blue-700 px-2 py-0.5 rounded-full text-[10px]">
                      Meaning: {mem.meaning}/5
                    </span>
                  )}
                </div>
                {mem.image && (
                  <img
                    src={mem.image}
                    alt="Memory"
                    className="my-2 w-full h-32 object-cover rounded-md shadow"
                  />
                )}
                <p className="italic text-gray-600 line-clamp-2">
                  ‚Äú{mem.journal.slice(0, 200)}...‚Äù
                </p>
                <p className="text-[10px] text-gray-400 mt-1">{mem.location?.description}</p>
                {Array.isArray(mem.tags) && mem.tags.length > 0 && (
                  <div className="flex flex-wrap gap-1 mt-2">
                    {mem.tags.map((tag, idx) => (
                      <span
                        key={idx}
                        className="bg-indigo-100 text-indigo-700 text-[10px] px-2 py-0.5 rounded-full"
                      >
                        {tag.name || tag.email?.split("@")[0]}
                      </span>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SparkCard;
