import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => ({
          ...m,
          dateFormatted: new Date(m.createdAt._seconds * 1000).toLocaleDateString("en-US", {
            month: "short",
            day: "numeric",
            year: "numeric",
          }),
        }));
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  // Group memories by date
  const groupedMemories = memories.reduce((acc, mem) => {
    if (!acc[mem.dateFormatted]) acc[mem.dateFormatted] = [];
    acc[mem.dateFormatted].push(mem);
    return acc;
  }, {});

  return (
    <div className="flex flex-col gap-24 max-w-6xl mx-auto px-4 pt-10 relative">
      {Object.entries(groupedMemories).map(([date, group], i) => (
        <div key={i} className="relative w-full">
          <div className="flex items-center gap-4">
            {/* Date Tag */}
            <div className="relative shrink-0">
              <div className="absolute top-1/2 right-full mr-3 transform -translate-y-1/2 z-10">
                <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md">
                  {date}
                </div>
              </div>

              {/* Thread from Date to First Card */}
              <div className="absolute top-1/2 right-0 h-[2px] bg-blue-300 opacity-70 z-0 rounded-full pointer-events-none"
                   style={{ width: "40px", transform: "translateY(-50%)" }}
              />
            </div>

            {/* Memory Row */}
            <div className="flex items-center gap-16">
              {group.map((memory, j) => (
                <div key={j} className="relative">
                  <MemoryCard memory={memory} />

                  {/* Thread between cards */}
                  {j !== group.length - 1 && (
                    <div className="absolute top-1/2 left-full ml-4 h-[2px] w-16 bg-blue-300 opacity-70 transform -translate-y-1/2 z-0 rounded-full" />
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
