import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "./components/SideBar/SideBar";
import HomeFeed from "./pages/HomeFeed/HomeFeed";
import CreateMemory from "./pages/CreateMemory/CreateMemory";
import Chat from "./pages/Chat/Chat";
import AuthPage from "./pages/Auth/AuthPage";

function App() {
  const token = localStorage.getItem("token");
  const username = localStorage.getItem("username");

  return (
    <Router>
      <Routes>
        {/* ✅ Always show AI onboarding at /auth */}
        <Route path="/auth" element={<AuthPage />} />

        {/* ✅ Default route "/" → redirect to /auth or /[username]/feed */}
        <Route
          path="/"
          element={
            token && username
              ? <Navigate to={`/${username}/feed`} />
              : <Navigate to="/auth" />
          }
        />

        {/* ✅ Protected Routes using actual username */}
        {token && username && (
          <>
            <Route
              path={`/${username}/feed`}
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <HomeFeed />
                  </div>
                </div>
              }
            />
            <Route
              path={`/${username}/create`}
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <CreateMemory />
                  </div>
                </div>
              }
            />
            <Route
              path={`/${username}/chat`}
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <Chat />
                  </div>
                </div>
              }
            />
          </>
        )}

        {/* ✅ Catch-all redirect */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
