import React from "react";

const MemoryCard = ({ memory }) => {
  return (
    <div className="relative bg-gradient-to-b from-[#e0f7ff] via-white to-[#f0eaff] p-6 rounded-[3rem] shadow-2xl w-full max-w-xs mx-auto border border-blue-100 ring-1 ring-blue-200 backdrop-blur-md overflow-hidden">
      {/* Background Glow */}
      <div className="absolute inset-0 blur-2xl bg-blue-200 opacity-20 pointer-events-none" />

      <div className="relative flex flex-col items-center space-y-4 z-10">
        {/* Capsule Image View */}
        <div className="overflow-hidden rounded-3xl h-56 w-full border-4 border-white shadow-md">
          <img
            src={memory.image}
            alt="memory"
            className="w-full h-full object-cover"
          />
        </div>

        {/* Story */}
        <div className="text-center space-y-1 px-2">
          <p className="text-[0.95rem] text-gray-800 leading-relaxed font-serif italic">
            ‚Äú{memory.journal}‚Äù
          </p>

          {/* Mood & Vibe */}
          {(memory.mood || memory.vibe) && (
            <div className="flex justify-center gap-2 mt-2 flex-wrap">
              {memory.mood && (
                <span className="px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-[11px] font-medium shadow-sm">
                  üß† Mood: <span className="capitalize">{memory.mood}</span>
                </span>
              )}
              {memory.vibe && (
                <span className="px-3 py-1 bg-purple-100 text-purple-700 rounded-full text-[11px] font-medium shadow-sm">
                  üåà Vibe: <span className="capitalize">{memory.vibe}</span>
                </span>
              )}
            </div>
          )}

          {/* Date */}
          <p className="text-[11px] text-gray-500 mt-1">{memory.date}</p>

          {/* Location */}
          {memory.city && memory.country && (
            <p className="text-[11px] text-blue-600 font-semibold tracking-wide mt-1">
              üìç {memory.city}, {memory.country}
            </p>
          )}
        </div>

        {/* Tags */}
        {memory.tags.length > 0 && (
          <div className="flex flex-wrap justify-center gap-2 text-xs text-blue-700 font-medium">
            <span className="text-[11px] text-gray-500">with</span>
            {memory.tags.map((tag, i) => (
              <span
                key={i}
                className="bg-blue-100 px-2 py-1 rounded-full shadow"
              >
                {tag}
              </span>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MemoryCard;
