import React, { useState } from "react";
import API from "../../utils/api";

const PROMPT_TEMPLATES = [
  "Celebrated something special üéâ",
  "Shared with someone I care about üíï",
  "Solo moment of peace üßò",
  "Late-night cravings üåô",
  "A cozy rainy-day meal ‚òî",
  "It reminded me of home üè°",
];

const MemoryForm = ({ onAddMemory }) => {
  const [imageFile, setImageFile] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [tags, setTags] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const localUrl = URL.createObjectURL(file);
      setImageFile(file);
      setImageUrl(localUrl);
    }
  };

  const handlePromptSelect = (prompt) => {
    setSelectedPrompt(prompt);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!imageFile || !selectedPrompt) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64Image = reader.result;

      const memory = {
        image: base64Image,
        journal: selectedPrompt,
        tags: tags.split(",").map((tag) => tag.trim()),
      };

      try {
        const res = await API.post("/memories", memory);
        onAddMemory(res.data.memory);
        setImageFile(null);
        setImageUrl(null);
        setSelectedPrompt("");
        setTags("");
      } catch (err) {
        console.error("‚ùå Failed to save memory:", err);
      }
    };

    reader.readAsDataURL(imageFile);
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4">
      <label className="text-sm font-medium">üì∏ Upload a photo</label>
      <input type="file" accept="image/*" onChange={handleImageChange} />

      {imageUrl && (
        <img src={imageUrl} alt="preview" className="rounded-lg max-h-64 object-contain border" />
      )}

      <div className="space-y-2">
        <p className="text-sm text-gray-600">‚ú® Pick a feeling that fits this meal</p>
        <div className="flex flex-wrap gap-2">
          {PROMPT_TEMPLATES.map((prompt) => (
            <button
              key={prompt}
              type="button"
              onClick={() => handlePromptSelect(prompt)}
              className={`px-3 py-1 rounded-full text-sm border transition ${
                selectedPrompt === prompt
                  ? "bg-pink-500 text-white border-pink-600"
                  : "bg-white text-gray-700 border-gray-300 hover:bg-gray-100"
              }`}
            >
              {prompt}
            </button>
          ))}
        </div>
        {selectedPrompt && (
          <p className="text-center italic text-pink-600 mt-1">"{selectedPrompt}"</p>
        )}
      </div>

      <input
        type="text"
        placeholder="Tag friends (comma-separated)"
        className="p-3 border border-gray-300 rounded-md text-sm"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

      <button
        type="submit"
        className="bg-gradient-to-r from-pink-400 to-yellow-400 hover:from-pink-500 hover:to-yellow-500 text-white font-semibold py-2 px-4 rounded-full shadow-md text-sm w-fit mx-auto"
      >
        ‚ú® Save Memory
      </button>
    </form>
  );
};

export default MemoryForm;
