import React, { useState } from "react";
import GlobalHeader from "../../components/GlobalHeader/GlobalHeader";
import MemoryForm from "../../components/MemoryForm/MemoryForm";
import AIAgentBubble from "../../components/AIAgentBubble/AIAgentBubble";

const CreateMemory = () => {
  const [memories, setMemories] = useState([]);
  const [aiSuggestion, setAISuggestion] = useState(null); // partner name if thread suggested

  const handleAddMemory = (memory) => {
    setMemories([memory, ...memories]);
  };

  const handleThreadSuggestion = (partnerName) => {
    setAISuggestion(partnerName);
  };

  const handleAIAccept = () => {
    window.location.href = `/profile/${aiSuggestion}`;
  };

  return (
    <>
      <GlobalHeader />
      <div className="max-w-2xl mx-auto">
        <h2 className="text-2xl font-semibold mb-4">ðŸ“· Create a New Memory</h2>
        <MemoryForm onAddMemory={handleAddMemory} onThreadSuggestion={handleThreadSuggestion} />
      </div>
      {aiSuggestion && (
        <AIAgentBubble
          partnerName={aiSuggestion}
          onConfirm={handleAIAccept}
          onClose={() => setAISuggestion(null)}
        />
      )}
    </>
  );
};

export default CreateMemory;
