import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "./components/SideBar/SideBar";
import HomeFeed from "./pages/HomeFeed/HomeFeed";
import CreateMemory from "./pages/CreateMemory/CreateMemory";
import Chat from "./pages/Chat/Chat";
import AuthPage from "./pages/Auth/AuthPage";

function App() {
  const token = localStorage.getItem("token");
  const userEmail = localStorage.getItem("userEmail");
  const userId = userEmail ? userEmail.split("@")[0] : null;

  return (
    <Router>
      <Routes>
        {/* ✅ Always show AI onboarding at /auth */}
        <Route path="/auth" element={<AuthPage />} />

        {/* ✅ Default route "/" → redirect to /auth or /[user]/feed */}
        <Route
          path="/"
          element={
            token && userId
              ? <Navigate to={`/${userId}/feed`} />
              : <Navigate to="/auth" />
          }
        />

        {/* ✅ Protected Routes */}
        {token && userId && (
          <>
            <Route
              path={`/${userId}/feed`}
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <HomeFeed />
                  </div>
                </div>
              }
            />
            <Route
              path="/create"
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <CreateMemory />
                  </div>
                </div>
              }
            />
            <Route
              path="/chat"
              element={
                <div className="flex min-h-screen">
                  <Sidebar />
                  <div className="flex-1 p-4">
                    <Chat />
                  </div>
                </div>
              }
            />
          </>
        )}

        {/* ✅ Catch-all redirect */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
