import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import MemoryCard from "../../components/MemoryCard/MemoryCard";

const HomeFeed = () => {
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    const fetchMemories = async () => {
      try {
        const res = await API.get("/memories");
        const formatted = res.data.memories.map((m) => ({
          ...m,
          dateFormatted: new Date(m.createdAt._seconds * 1000).toLocaleDateString("en-US", {
            month: "short",
            day: "numeric",
            year: "numeric",
          }),
        }));
        setMemories(formatted);
      } catch (err) {
        console.error("❌ Failed to load memories:", err);
      }
    };

    fetchMemories();
  }, []);

  return (
    <div className="flex flex-col gap-20 max-w-6xl mx-auto px-4 pt-10 relative">
      {memories.map((memory, i) => (
        <div key={i} className="relative flex items-center gap-6">
          {/* Date Bubble + Thread */}
          <div className="relative w-32 shrink-0 text-right">
            {/* Date Bubble */}
            <div className="bg-blue-100 text-blue-700 text-xs font-medium px-3 py-1 rounded-full shadow-md inline-block z-10 relative">
              {memory.dateFormatted}
            </div>

            {/* Thread Line from date to capsule */}
            <div
              className="absolute top-1/2 left-full ml-2 h-[2px] bg-gradient-to-r from-blue-300 to-blue-100 transform -translate-y-1/2 z-0 rounded-full pointer-events-none"
              style={{ width: "calc(100% - 10rem)" }}
            />
          </div>

          {/* Memory Capsule */}
          <div className="flex-1 z-10">
            <MemoryCard memory={memory} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default HomeFeed;
