import React from "react";
import clsx from "clsx";

const emotionFaceMap = {
  NEUTRAL: "üåù",
  HAPPY: "üòä",
  FRIENDLY: "üòÑ",
  SAD: "üò¢",
  MAD: "üò†",
  EXCITED: "ü§©",
  LOVING: "ü•∞"
};

const bgColorMap = {
  NEUTRAL: "bg-gray-100",
  HAPPY: "bg-yellow-100",
  FRIENDLY: "bg-green-100",
  SAD: "bg-blue-100",
  MAD: "bg-red-100",
  EXCITED: "bg-pink-100",
  LOVING: "bg-rose-100"
};

const LunrAvatar = ({ emotion = "NEUTRAL", size = 10 }) => {
  const emoji = emotionFaceMap[emotion] || "üåù";
  const bgColor = bgColorMap[emotion] || "bg-gray-100";

  return (
    <div
      className={clsx(
        "flex items-center justify-center rounded-full text-lg shadow-md animate-floatSoft",
        `w-${size} h-${size}`,
        bgColor
      )}
    >
      <span className="animate-emotionBlink">{emoji}</span>
    </div>
  );
};

export default LunrAvatar;
