import React from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const { pathname } = useLocation();
  const user = JSON.parse(localStorage.getItem("user"));
  const username = user?.name;

  const navItem = (path, label, icon) => (
    <Link
      to={path}
      className={`flex items-center gap-3 px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-200 transition ${
        pathname === path ? "bg-gray-300" : ""
      }`}
    >
      <span>{icon}</span>
      <span>{label}</span>
    </Link>
  );

  return (
    <div className="w-52 bg-white shadow-md p-4 space-y-4">
      <h1 className="text-xl font-bold text-center"></h1>
      {navItem(`/${username}/feed`, "Home", "üè†")}
      {navItem(`/${username}/create`, "Create Memory", "‚ûï")}
      {navItem(`/${username}/chat`, "Chat", "üí¨")}
      {navItem(`/${username}/sparks`, "Sparks", "‚ú®")}
    </div>
  );
};

export default Sidebar;
