import React, { useState } from "react";

const MemoryForm = ({ onAddMemory }) => {
  const [image, setImage] = useState(null);
  const [journal, setJournal] = useState("");
  const [tags, setTags] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setImage(url);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!image || !journal) return;
    const memory = {
      image,
      journal,
      tags: tags.split(",").map(tag => tag.trim()),
      date: new Date().toLocaleDateString(),
    };
    onAddMemory(memory);
    setImage(null);
    setJournal("");
    setTags("");
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4 p-4">
      <input type="file" accept="image/*" onChange={handleImageChange} className="text-sm" />

      {image && <img src={image} alt="preview" className="rounded-md h-48 object-cover" />}

      <textarea
        placeholder="Write your food story..."
        className="p-2 border rounded-md text-sm"
        value={journal}
        onChange={(e) => setJournal(e.target.value)}
      />

      <input
        type="text"
        placeholder="Tag friends (comma-separated)"
        className="p-2 border rounded-md text-sm"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

      <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded-md text-sm">
        Save Memory
      </button>
    </form>
  );
};

export default MemoryForm;
