import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "./components/SideBar/SideBar";
import HomeFeed from "./pages/HomeFeed/HomeFeed";
import CreateMemory from "./pages/CreateMemory/CreateMemory";
import Chat from "./pages/Chat/Chat";
import AuthPage from "./pages/Auth/AuthPage";

function App() {
  const token = localStorage.getItem("token");
  const userId = localStorage.getItem("userEmail")?.split("@")[0];

  return (
    <Router>
      <Routes>
        {/* ✅ Always show AI onboarding first */}
        <Route path="/auth" element={<AuthPage />} />

        {/* ✅ Redirect to /auth if not logged in */}
        {!token ? (
          <Route path="*" element={<Navigate to="/auth" />} />
        ) : (
          <Route
            path="*"
            element={
              <div className="flex min-h-screen">
                <Sidebar />
                <div className="flex-1 p-4">
                  <Routes>
                    <Route path={`/${userId}/feed`} element={<HomeFeed />} />
                    <Route path="/create" element={<CreateMemory />} />
                    <Route path="/chat" element={<Chat />} />
                  </Routes>
                </div>
              </div>
            }
          />
        )}
      </Routes>
    </Router>
  );
}

export default App;
