import React from "react";

const MemoryCard = ({ memory }) => {
  return (
    <div className="bg-white rounded-lg shadow p-4 mb-4">
      <img src={memory.image} alt="memory" className="rounded-md h-48 w-full object-cover" />
      <p className="text-gray-700 mt-2 text-sm">{memory.journal}</p>
      <p className="text-xs text-gray-500 mt-1">{memory.date}</p>
      {memory.tags.length > 0 && (
        <div className="flex flex-wrap mt-2 gap-2 text-xs text-blue-500">
          {memory.tags.map((tag, i) => (
            <span key={i}>@{tag}</span>
          ))}
        </div>
      )}
    </div>
  );
};

export default MemoryCard;
