import React, { useState } from "react";
import API from "../../utils/api";

const moodOptions = ["cozy", "excited", "nostalgic", "peaceful"];
const occasionOptions = ["solo", "family", "date", "celebration"];
const vibeOptions = ["warm", "spicy", "simple", "comforting"];

const MemoryForm = ({ onAddMemory }) => {
  const [imageFile, setImageFile] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);
  const [story, setStory] = useState("");
  const [tags, setTags] = useState("");
  const [mood, setMood] = useState("");
  const [occasion, setOccasion] = useState("");
  const [vibe, setVibe] = useState("");
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      setImageUrl(URL.createObjectURL(file));
    }
  };

  const handleGenerateStory = async () => {
    if (!mood || !occasion || !vibe) return;
    setLoading(true);
    try {
      const res = await API.post("/memories/generate", { mood, occasion, vibe });
      setStory(res.data.story);
    } catch (err) {
      console.error("âŒ GPT error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!imageFile || !story) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      try {
        const res = await API.post("/memories", {
          image: reader.result,
          journal: story,
          tags: tags.split(",").map((t) => t.trim()),
        });
        onAddMemory(res.data.memory);
        resetForm();
      } catch (err) {
        console.error("âŒ Save error:", err);
      }
    };

    reader.readAsDataURL(imageFile);
  };

  const resetForm = () => {
    setImageFile(null);
    setImageUrl(null);
    setStory("");
    setTags("");
    setMood("");
    setOccasion("");
    setVibe("");
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4">
      <input type="file" accept="image/*" onChange={handleImageChange} />
      {imageUrl && <img src={imageUrl} className="rounded-md max-h-64 object-contain" />}

      <div className="flex flex-wrap gap-2">
        {moodOptions.map((m) => (
          <button
            type="button"
            key={m}
            onClick={() => setMood(m)}
            className={`px-3 py-1 rounded-full ${mood === m ? "bg-blue-500 text-white" : "bg-gray-200"}`}
          >
            Mood: {m}
          </button>
        ))}
      </div>

      <div className="flex flex-wrap gap-2">
        {occasionOptions.map((o) => (
          <button
            type="button"
            key={o}
            onClick={() => setOccasion(o)}
            className={`px-3 py-1 rounded-full ${occasion === o ? "bg-green-500 text-white" : "bg-gray-200"}`}
          >
            Occasion: {o}
          </button>
        ))}
      </div>

      <div className="flex flex-wrap gap-2">
        {vibeOptions.map((v) => (
          <button
            type="button"
            key={v}
            onClick={() => setVibe(v)}
            className={`px-3 py-1 rounded-full ${vibe === v ? "bg-pink-500 text-white" : "bg-gray-200"}`}
          >
            Vibe: {v}
          </button>
        ))}
      </div>

      <button
        type="button"
        onClick={handleGenerateStory}
        className="bg-black text-white px-4 py-2 rounded w-fit"
      >
        {loading ? "Generating..." : "âœ¨ Generate Story"}
      </button>

      {story && <p className="italic bg-gray-50 p-2 rounded text-center">"{story}"</p>}

      <input
        type="text"
        placeholder="Tags (comma-separated)"
        className="p-2 border rounded"
        value={tags}
        onChange={(e) => setTags(e.target.value)}
      />

      <button
        type="submit"
        className="bg-yellow-500 text-white px-4 py-2 rounded w-fit mx-auto"
      >
        ðŸ’¾ Save Memory
      </button>
    </form>
  );
};

export default MemoryForm;
