import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import GlobalHeader from "../../components/GlobalHeader/GlobalHeader";

const MemorySparks = () => {
  const currentUser = JSON.parse(localStorage.getItem("user"));
  const [sparks, setSparks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSparks = async () => {
      try {
        const res = await API.get("/memories/sparks");
        setSparks(res.data.sparks);
      } catch (err) {
        console.error("❌ Failed to fetch sparks:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchSparks();
  }, []);

  return (
    <>
      <GlobalHeader />
      <div className="max-w-5xl mx-auto py-10 px-4">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
          ✨ Memory Sparks
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Loading sparks...</p>
        ) : sparks.length === 0 ? (
          <p className="text-center text-gray-500">No sparks right now. Be the first to start one!</p>
        ) : (
          <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
            {sparks.map((spark) => (
              <div
                key={spark.id}
                className="bg-white/90 backdrop-blur-md border border-blue-100 rounded-2xl p-5 shadow-xl hover:shadow-2xl transition duration-300 flex flex-col justify-between"
              >
                <img
                  src={spark.image}
                  alt="Memory Preview"
                  className="w-full h-48 object-cover rounded-xl mb-3"
                />

                <p className="text-sm text-gray-600 italic mb-2">
                  “{spark.journal.slice(0, 120)}{spark.journal.length > 120 ? "..." : ""}”
                </p>

                <div className="text-xs text-blue-700 font-semibold mb-2">
                  Vibe: {spark.vibe} • Mood: {spark.mood}
                </div>
                <p className="text-xs text-gray-500 mb-2">
                  {spark.city}, {spark.country} • {spark.time}
                </p>

                {/* 👤 Creator Avatar + Name */}
                <div
                  className="flex items-center gap-2 mt-2 cursor-pointer"
                  onClick={() => {
                    window.location.href = `/profile/${spark.creatorName}`;
                  }}
                >
                  <img
                    src={`https://api.dicebear.com/7.x/thumbs/svg?seed=${spark.creatorName}`}
                    alt={spark.creatorName}
                    className="w-6 h-6 rounded-full border shadow-sm"
                  />
                  <span className="text-xs text-gray-700 font-medium">{spark.creatorName}</span>
                </div>

                {/* ✅ Respond Button */}
                <button
                  className="mt-4 w-full bg-gradient-to-r from-green-500 to-teal-500 text-white py-2 rounded-full font-semibold hover:from-green-600 hover:to-teal-600 transition"
                  onClick={() => {
                    window.location.href = `/create?respondTo=${spark.id}`;
                  }}
                >
                  🧩 Respond to This Spark
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default MemorySparks;
